import React from 'react';
import { Image, View } from 'react-native';

import { styles } from './styles';
import DiscordSvg from '../../assets/discord.svg';

const { CDN_IMAGE } = process.env;

type Props = {
    guildId: string;
    iconId: string | null;
}

export function GuildIcon({ guildId, iconId } : Props) {

    const uri = `${CDN_IMAGE}/icons/${guildId}/${iconId}.png`;
    
    //const uri = 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/198142ac-f410-423a-bf0b-34c9cb5d9609/dbtif5j-60306864-d6b7-44b6-a9ff-65e8adcfb911.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..pRh5DK_cxlZ6SxVPqoUSsSNo1fqksJVP6ECGVUi6kmE';

    return (

        <View style={styles.container}>
        {
            iconId ?
            
            <Image 
                source={{ uri }}
                style={styles.image}
                resizeMode="cover"
            />
            :
            <DiscordSvg 
                width={40}
                height={40}
            />
        }
        </View>

    );

};